void Debbug_Toggle(uint32_t cycles){
    for(uint32_t i = 0; i < cycles; i++)
        __NOP(); // no operations

    led_state(toggle);
}

int main(void){
	//Clock init
//	HSI16_init();
	HSI_PLL48_init();
//	HSE32_CPU_init();

	//Green led init
	led_init();

	//USART init
	USART1_Init(); //115200
	printf("System start - usart init check\r\n");

	//Radio init
	rfl_subghz_init();
	rfl_subghz_initRx();
	uint8_t payload[64] = "MEGA GIGA MASZT 5G\r\n";
	rfl_subghz_transmit(payload);
	Debbug_Toggle(10000000);
	rfl_subghz_setRxContinous();
//	rfl_subghz_initTx();

	while(1){
		Debbug_Toggle(20000000);

	}


}

